import React, { useState } from "react";
import { ArrowRight, Copy } from "lucide-react";
import Marquee from "react-fast-marquee";

export default function Home() {
  const [copied, setCopied] = useState(false);

  const contractAddress = "YourContractAddressHere";

  const copyToClipboard = () => {
    navigator.clipboard.writeText(contractAddress);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-pink-900 via-black to-pink-900 text-white font-sans">
      {/* Hero Section */}
      <section className="flex flex-col items-center justify-center text-center px-4 py-20 space-y-6">
        <img
          src="/heartcoin-logo.png"
          alt="HeartCoin Logo"
          className="w-48 h-48"
        />
        <h1 className="text-6xl font-extrabold tracking-wide">HEARTCOIN SOLANA</h1>
        <div className="flex flex-wrap gap-4 justify-center">
          <button className="bg-pink-600 hover:bg-pink-700 transition px-8 py-3 rounded-full font-semibold">
            Buy Now
          </button>
          <button className="border border-white hover:bg-white hover:text-black transition px-8 py-3 rounded-full flex items-center justify-center gap-2 font-semibold">
            Chart <ArrowRight className="w-5 h-5" />
          </button>
        </div>
      </section>

      {/* Marquee */}
      <Marquee className="bg-pink-700 text-white py-3 text-xl font-semibold tracking-wide">
        $HEART - LOVE IS THE TRUE CURRENCY ðŸ’— $HEART - SPREAD LOVE ðŸ’— $HEART - NO RUGS, JUST HUGS ðŸ’—
      </Marquee>

      {/* Contract Address Section */}
      <section className="py-16 px-6 text-center">
        <h2 className="text-3xl font-bold mb-4">Contract Address</h2>
        <div className="inline-flex items-center bg-gray-900 rounded-full px-6 py-3 select-all cursor-pointer max-w-lg mx-auto"
          onClick={copyToClipboard}
          title="Click to copy"
        >
          <code className="text-lg">{contractAddress}</code>
          <Copy className="ml-3 w-5 h-5 text-pink-500" />
        </div>
        {copied && <p className="mt-2 text-pink-400 font-medium">Copied to clipboard!</p>}
      </section>

      {/* Links Section */}
      <section className="px-6 py-12 text-center">
        <h2 className="text-3xl font-bold mb-6">Join the Community</h2>
        <div className="flex justify-center gap-6 flex-wrap">
          <a
            href="https://t.me/heartcoinportal"
            target="_blank"
            rel="noopener noreferrer"
            className="bg-blue-500 hover:bg-blue-600 text-white px-8 py-3 rounded-full text-lg font-semibold"
          >
            Telegram
          </a>
          <a
            href="https://dexscreener.com/solana/heartcoin"
            target="_blank"
            rel="noopener noreferrer"
            className="bg-green-500 hover:bg-green-600 text-white px-8 py-3 rounded-full text-lg font-semibold"
          >
            View on DexScreener
          </a>
        </div>
      </section>

      {/* Footer */}
      <footer className="py-8 text-center text-sm text-gray-400 select-none">
        Â© 2025 HeartCoin. All Love Reserved.
      </footer>
    </div>
  );
}
